import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import ContactInfo from '../../Data/ContactInfo';
import './contacto.css';
import FormularioContacto from '../../components/FormContacto/FormularioContacto';

const Contacto = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleModalToggle = () => setIsModalOpen(!isModalOpen);

  return (
    <section className='contacto-section' id="contacto">
      <div className='contacto-container'>
        <h2 className='contacto-title texto-parpadeante'>CONT√ÅCTANOS</h2>

        <div className='contacto-datos'>
          {ContactInfo.map((item, index) => (
            <p key={index}><strong>{item.label}:</strong> {item.value}</p>
          ))}
        </div>

        <div className='contacto-boton'>
          <button className="contacto-btn" onClick={handleModalToggle}>
            Quiero que me contacten
          </button>
        </div>
      </div>

      {isModalOpen && <FormularioContacto handleModalToggle={handleModalToggle} />}
    </section>
  );
};

export default Contacto;
